<template>
  <div :class="ui.wrapper">
    <textarea
      :id="name"
      ref="textarea"
      :value="modelValue"
      :name="name"
      :rows="rows"
      :required="required"
      :disabled="disabled"
      :placeholder="placeholder"
      class="form-textarea"
      :class="textareaClass"
      v-bind="$attrs"
      @input="onInput"
      @blur="onBlur"
    />
  </div>
</template>

<script>
import { ref, computed, watch, onMounted, nextTick, defineComponent } from "vue";
import { defu } from "defu";
import { classNames } from "../../utils";
import { useFormEvents } from "../../composables/useFormEvents";
import { useAppConfig } from "#imports";
import appConfig from "#build/app.config";
export default defineComponent({
  inheritAttrs: false,
  props: {
    modelValue: {
      type: [String, Number],
      default: ""
    },
    name: {
      type: String,
      default: null
    },
    placeholder: {
      type: String,
      default: null
    },
    required: {
      type: Boolean,
      default: false
    },
    disabled: {
      type: Boolean,
      default: false
    },
    rows: {
      type: Number,
      default: 3
    },
    autoresize: {
      type: Boolean,
      default: false
    },
    autofocus: {
      type: Boolean,
      default: false
    },
    resize: {
      type: Boolean,
      default: false
    },
    padded: {
      type: Boolean,
      default: true
    },
    size: {
      type: String,
      default: () => appConfig.ui.textarea.default.size,
      validator(value) {
        return Object.keys(appConfig.ui.textarea.size).includes(value);
      }
    },
    color: {
      type: String,
      default: () => appConfig.ui.textarea.default.color,
      validator(value) {
        return [...appConfig.ui.colors, ...Object.keys(appConfig.ui.textarea.color)].includes(value);
      }
    },
    variant: {
      type: String,
      default: () => appConfig.ui.textarea.default.variant,
      validator(value) {
        return [
          ...Object.keys(appConfig.ui.textarea.variant),
          ...Object.values(appConfig.ui.textarea.color).flatMap((value2) => Object.keys(value2))
        ].includes(value);
      }
    },
    ui: {
      type: Object,
      default: () => appConfig.ui.textarea
    }
  },
  emits: ["update:modelValue", "blur"],
  setup(props, { emit }) {
    const textarea = ref(null);
    const appConfig2 = useAppConfig();
    const ui = computed(() => defu({}, props.ui, appConfig2.ui.textarea));
    const { emitFormBlur } = useFormEvents();
    const autoFocus = () => {
      if (props.autofocus) {
        textarea.value?.focus();
      }
    };
    const autoResize = () => {
      if (props.autoresize) {
        if (!textarea.value) {
          return;
        }
        textarea.value.rows = props.rows;
        const styles = window.getComputedStyle(textarea.value);
        const paddingTop = parseInt(styles.paddingTop);
        const paddingBottom = parseInt(styles.paddingBottom);
        const padding = paddingTop + paddingBottom;
        const lineHeight = parseInt(styles.lineHeight);
        const { scrollHeight } = textarea.value;
        const newRows = (scrollHeight - padding) / lineHeight;
        if (newRows > props.rows) {
          textarea.value.rows = newRows;
        }
      }
    };
    const onInput = (event) => {
      autoResize();
      emit("update:modelValue", event.target.value);
    };
    const onBlur = (event) => {
      emitFormBlur();
      emit("blur", event);
    };
    onMounted(() => {
      setTimeout(() => {
        autoFocus();
      }, 100);
    });
    watch(() => props.modelValue, () => {
      nextTick(autoResize);
    });
    onMounted(() => {
      setTimeout(() => {
        autoFocus();
        autoResize();
      }, 100);
    });
    const textareaClass = computed(() => {
      const variant = ui.value.color?.[props.color]?.[props.variant] || ui.value.variant[props.variant];
      return classNames(
        ui.value.base,
        ui.value.rounded,
        ui.value.placeholder,
        ui.value.size[props.size],
        props.padded ? ui.value.padding[props.size] : "p-0",
        variant?.replaceAll("{color}", props.color),
        !props.resize && "resize-none"
      );
    });
    return {
      // eslint-disable-next-line vue/no-dupe-keys
      ui,
      textarea,
      textareaClass,
      onInput,
      onBlur
    };
  }
});
</script>
